This should fix the issue. The modified gpt4-image-alt-text.js should now look like this:javascriptCopy codeconst { createHigherOrderComponent } = wp.compose;
const { createElement, Fragment, useState, useEffect } = wp.element;
const { addFilter } = wp.hooks;
const { InspectorControls } = wp.blockEditor;
const { Button, PanelBody } = wp.components;

const addGPT4Button = createHigherOrderComponent((BlockEdit) => {
    return function (props) {
        const [apiKey, setApiKey] = useState(null);

        useEffect(() => {
            const fetchApiKey = async () => {
