你好！以下是我为你编写的一个简单的 Go 语言程序，它可以按照你的要求爬取博客文章，并将结果输出到一个名为 "xargin_blogs.md" 的文件中。goCopy codepackage main

import (
	"fmt"
	"io/ioutil"
	"net/http"
	"os"
	"strings"
	"time"

	"github.com/PuerkitoBio/goquery"
)

const baseURL = "https://xargin.com"
const startURL = "https://xargin.com/sarama-producer-hang-another-case/"

type BlogPost struct {
	Title       string
	PublishDate string
	ReadTime    string
	URL         string
}

func main() {
	blogPosts := crawlBlogPosts(startURL)

	file, err := os.Create("xargin_blogs.md")
	if err != nil {
		panic(err)
	}
	defer file.Close()

	fmt.Fprintln(file, "| 标题 | 发表时间 | 阅读时间 | URL |")
	fmt.Fprintln(file, "| --- | --- | --- | --- |")

	for _, post := range blogPosts {
		fmt.Fprintf(file, "| %s | %s | %s | %s |\n", post.Title, post.PublishDate, post.ReadTime, post.URL)
	}
}

func crawlBlogPosts(url string) []BlogPost {
	var posts []BlogPost

	for {
		post, prevURL, err := getBlogPost(url)
		if err != nil {
			break
		}

		posts = append(posts, *post)

		if prevURL == "" {
			break
		}

		url = baseURL + prevURL
		time.Sleep(1 * time.Second) // 为了避免频繁请求，设置一个简单的延迟
	}

	return posts
}

func getBlogPost(url string) (*BlogPost, string, error) {
	resp, err := http.Get(url)
	if err != nil {
		return nil, "", err
	}
	defer resp.Body.Close()

	doc, err := goquery.NewDocumentFromReader(resp.Body)
	if err != nil {
		return nil, "", err
	}

	title := doc.Find(".post-title").Text()
	publishDate, _ := doc.Find(".post-meta-date time").Attr("datetime")
	readTime := strings.TrimSpace(doc.Find(".post-meta-item.post-meta-length").Text())
	prevURL, _ := doc.Find(".navigation-item.navigation-previous a").Attr("href")

	post := &BlogPost{
		Title:       title,
		PublishDate: publishDate,
		ReadTime:    readTime,
		URL:         url,
	}

	return post, prevURL, nil
}
