from bs4 import BeautifulSoup
from urllib.parse import urljoin, urlparse, urlunparse
import sys
import re

if len(sys.argv) != 2:
    print("Usage: python sitemap_generator.py [url]")
    sys.exit()

starting_url = sys.argv[1]

if not urlparse(starting_url).scheme:
    starting_url = "http://" + starting_url

visited_urls = set()
queue = [(starting_url, None)]

def strip_hash_fragment(url):
    parsed_url = urlparse(url)
    return urlunparse(parsed_url._replace(fragment=''))

def strip_trailing_slash(url):
    return url.rstrip('/')

def is_html_url(url):
    return not re.search(r'\.(pdf|jpg|jpeg|png|gif|bmp|ico|mp3|mp4|wav|zip|tar|gz|css|js)$', url, re.IGNORECASE)

def get_links(url, referrer):
    try:
        response = requests.get(url)
        if response.history and response.history[0].status_code == 301:
            # Skip processing redirected URLs that point to another domain
            redirected_domain = urlparse(response.url).netloc
            starting_domain = urlparse(starting_url).netloc
            if redirected_domain != starting_domain:
                print(f"Skipping redirected URL: {response.url} (referrer: {referrer if referrer else 'N/A'})")
                return []

        response.raise_for_status()
    except requests.exceptions.RequestException as e:
        print(f"Error: {e} (referrer: {referrer if referrer else 'N/A'})")
        return []

    if response.status_code == 404:
        print(f"Skipping 404: {url} (broken link on {referrer if referrer else 'N/A'})")
        return []

    soup = BeautifulSoup(response.text, 'html.parser')
    links = [a.get('href') for a in soup.find_all('a', href=True)]

    return links

with open("sitemap.txt", "w") as sitemap_file:
    while queue:
        current_url, referrer = queue.pop(0)
        current_url_no_fragment = strip_hash_fragment(current_url)
        current_url_no_trailing_slash = strip_trailing_slash(current_url_no_fragment)

        if current_url_no_trailing_slash not in visited_urls:
            visited_urls.add(current_url_no_trailing_slash)

            current_url_parsed = urlparse(current_url)
            starting_url_parsed = urlparse(starting_url)

            if current_url_parsed.netloc == starting_url_parsed.netloc and is_html_url(current_url_no_trailing_slash):
